                $quote->getShippingAddress()->setShippingMethod($shipping_code)->setCollectShippingRates(true);
            }
            //Check if applied for coupon
            //~ var_dump($couponCode);die('asd');
            if ($couponCode != '') {
                $quote->setCouponCode($couponCode)->collectTotals()->save();
                $coupon_status = 1;
