    public function tipOptions(){
        $objectManager = \Magento\Framework\App\ObjectManager::getInstance();
        $resource = $objectManager->get('Magento\Framework\App\ResourceConnection');
        $connection = $resource->getConnection();
        $tipOptions = "SELECT  * FROM amasty_extrafee_option where 1=1";
        $tipOptionsArr = $connection->fetchAll($tipOptions);
        $tipData = [];
        foreach($tipOptionsArr as $key=>$val){
            if($val['admin']!='Shopper Fees'){
            $tipData[$key]['option_id']= $val['entity_id'];
            $tipData[$key]['fee_id']= $val['fee_id'];
            $tipData[$key]['is_custom'] = ($val['price_type']=='fixed' || $val['price_type']=='percent')?false:true;
            $tipData[$key]['percent_value'] = $val['price'];
            $tipData[$key]['amount_value'] = ($val['price_type']=="fixed")?(int)$val['price']:0;
            $tipData[$key]['is_applied'] = ($val['default']==1)?true:false;
            $tipData[$key]['price_type'] = $val['price_type'];
            }
        }
        return $tipData;
    }
    
