    public function updateProductInOrder($orderId,$productQty,$orderitemidarray){
        
         
        $response=0;
        $objectManager = \Magento\Framework\App\ObjectManager::getInstance();
        $quoteToOrder = $objectManager
            ->create(
                'Magento\Quote\Model\Quote\Item\ToOrderItem'
            );

        $order = $objectManager
            ->create(
                'Magento\Sales\Model\Order'
            )->load($orderId);

        $quote = $objectManager
            ->create(
                '\Magento\Quote\Model\Quote'
            )->load($order->getQuoteId());

        $items = $quote->getAllItems();

        foreach ($items as $quoteItem) {
            $origOrderItem = $order->getItemByQuoteItemId($quoteItem->getId());
           // $orderItemId = $origOrderItem->getItemId();

            if(in_array($origOrderItem->getItemId(), $orderitemidarray)){

                //update quote item according your need
                $this->logger->info("Quote item updated   setQty  1=> ".$productQty);
                $quoteItem->setQty($productQty);

                $this->logger->info("Quote item updated 2=> ".$productQty);


                $quoteItem->save();
            }
        }
        $quote->collectTotals();
        $quote->save();


        $items = $quote->getAllItems();
        $taxAmount=$taxBaseAmount=0;
        foreach ($items as $quoteItem) {
            $orderItem = $quoteToOrder->convert($quoteItem);
            $origOrderItemNew = $order->getItemByQuoteItemId($quoteItem->getId());

            if ($origOrderItemNew) {
                $origOrderItemNew->addData($orderItem->getData());
            } else {
                if ($quoteItem->getParentItem()) {
                    $orderItem->setParentItem(
                        $order->getItemByQuoteItemId($orderItem->getParentItem()->getId())
                    );
                }
                $order->addItem($orderItem);
            }

            $response=1;
            $taxAmount=$taxAmount+$quoteItem->getTaxAmount();
            $taxBaseAmount=$taxBaseAmount+$quoteItem->getBaseTaxAmount();
        }

        $order->setSubtotal($quote->getSubtotal())
            ->setBaseSubtotal($quote->getBaseSubtotal())
            ->setGrandTotal($quote->getGrandTotal())
            ->setBaseGrandTotal($quote->getBaseGrandTotal())->setTaxAmount($taxAmount)->setBaseTaxAmount($taxBaseAmount);
        $quote->save();
        $order->save();
        
        //$orderData = $order->toArray();
       // print_r($orderData);
        
         
        return $response;

    }

