    public function updateOrder($picker_id,$orderid,$itemstatus,$product_sku,$order_quantity,$quantity,$product_id='',$old_product_id='',$old_product_sku='',$comment,$reason,$old_order_item_id='',$is_weighted=0){
        $updated=0;
        $new_order_item_id='';
        $action = false;
        $type = false;
        if($itemstatus=='refund' && $product_sku && $product_id){
            //$this->removeProductfromOrder($orderid,$product_id);  // MAKE IT ORDER ITEM  ID BASED
            $updated=1;
            if(!empty($reason) && $reason=="Other"){                
                $reason=$comment;
                $comment=NULL;
                $action="refund";
                $type="refund";                
            }else{
                $comment=NULL;
                $action="refund";
                $type="refund";
            }
        }
        else if($itemstatus=='add_item' && $product_sku && $product_id){
            $new_order_item_id=$this->addOrderProduct($orderid,$quantity,$product_id);
            $updated=1;
            $action="added new item";
            $type="add_item";
        }
        else if($itemstatus=='replace' && $product_sku && $product_id && $old_product_id && $old_product_sku){
            //$new_order_item_id=$this->addRemoveOrderProduct($orderid,$quantity,$product_id,$old_product_id);// MAKE IT ORDER ITEM  ID BASED
            $updated=1;
            $action="replace";
            $type="replace";
            if(empty($comment)){
                $comment='This item was replaced by your shopper.';
            }
        }
        else if($itemstatus=='found' && $product_sku && $product_id){
            $action=false;
            $type=false;
            if($quantity==$order_quantity){
                $updated=1;
            }else{
                if($is_weighted==0){
                    $itemstatus='found_update';
                    // $this->updateProductInOrder($orderid,$quantity,$product_id);// MAKE IT ORDER ITEM  ID BASED
                    $updated=1;
                    if(empty($comment)){
                        $comment='This item was out of stock or the required number of items were unavailable.';
                        $action="item not found";
                        $type="out_of_stock";                        
                    }
                }else{
                    $updated=$this->updateProductInOrder($orderid,$quantity,[$old_order_item_id]);// MAKE IT ORDER ITEM  ID BASED
                   // $updated=1;
                    if(!$updated){
                        $action="item not found";
                        $type="out_of_stock";
                    }
                }
            }
            
        }

        if($updated==1){
            $this->updatePackingHistory($picker_id,$orderid,$itemstatus,$product_sku,$order_quantity,$quantity,$old_product_sku,$comment,$reason,$new_order_item_id,$old_order_item_id, $product_id);
            $orders = $this->_orderData->getCollection()->addFieldToFilter('entity_id', $orderid);
            $customerId="";
            foreach ($orders as $order) {
                $customerId = $order->getCustomerId();
            }            
            
            $message = "Shopping Alert: Item Unavailable/Replaced \n\nAn item from your order is unavailable.";
            
            if($action){ 
                $notification_params=[
                    'order_id'=>$orderid,
                    'user_id'=>$customerId,
                    'user_type'=>'customer',
                    'notification_type'=>'normal',
                    'channel'=>$this->CUSTOMER_CHANNEL.$customerId,
                    'type'=>'order_packing_status',
                    'subject'=>"Order update",
                    'message'=>$message,
                    'platform'=>'ios',
                    'created_at'=>date('Y-m-d h:i:s'),
                    'updated_at'=>date('Y-m-d h:i:s'),
                    'readed'=>1
                ];
                           
                $this->pushNotificationBell($notification_params);
            }
        }
        
        return true;
    }

