    function createCachedFile($fileName,$q,$data,$temp='',$sub_temp=''){
        $dir = $temp."/mage--0/";
        $this->createDir($dir);
        if($sub_temp!=""){
            $dir = $temp."/mage--0/$sub_temp/";
            $this->createDir($dir);
        }
        $my_file = $dir.$fileName.str_replace(" ","_",$q).".json";
        $handle = @fopen($my_file, 'w+') or die('Cannot open file:  '.$my_file);
        $data=json_encode($data);
        fwrite($handle, $data);
        fclose($handle);
    }
    function readCachedFile($fileName,$q,$temp='',$sub_temp=''){
        if($sub_temp!=""){
            $my_file = $temp."/mage--0/$sub_temp/".$fileName.str_replace(" ","_",$q).".json";
        }else{
            $my_file = $temp."/mage--0/".$fileName.str_replace(" ","_",$q).".json";
        }
        if(file_exists($my_file))
        {
            $fileTime = filemtime($my_file);
            $time_diff = time() - $fileTime;
            if($time_diff > 3600*15 ){
                unlink($my_file);
            }
        }
        if(file_exists($my_file))
        {
            $handle = fopen($my_file, 'r');
            $data = fread($handle,filesize($my_file));
            if(isset($data))$data = json_decode($data,true);
            fclose($handle);
            return $data;
        }else return null;
    }

