    public function createInserQuery($table,$data){
        $keys=implode(",",array_keys($data));
        $values="'" . implode ( "', '", array_values($data) ) . "'";
        return $sql= "Insert into $table ($keys) VALUES($values)";
    }

    public function saveNotification($data){
        $data['message'] = addslashes(trim($data['message']));

        $objectManager = \Magento\Framework\App\ObjectManager::getInstance();
        $resource = $objectManager->get('Magento\Framework\App\ResourceConnection');
        $connection = $resource->getConnection();
        $sql=$this->createInserQuery('app_sessions',$data);
        $this->logger->info("Notification query =>" .$sql);
        $resultparent = $connection->query($sql);
        $lastInsertId = $connection->lastInsertId();

        if($lastInsertId){
            $sqlparent = "Select * FROM app_sessions WHERE id=$lastInsertId";
            $resultparent = $connection->fetchRow($sqlparent);
            if($resultparent){
                $resultparent['created_at']=$this->dateTimezone($resultparent['created_at']);
                $resultparent['updated_at']=$this->dateTimezone($resultparent['updated_at']);

            }
            return $resultparent ;
        }

    }

