    public function getFirstSlotOfPicker($picker_id){

        $objectManager = \Magento\Framework\App\ObjectManager::getInstance();
        $resource = $objectManager->get('Magento\Framework\App\ResourceConnection');
        $connection = $resource->getConnection();

        $currentTimestamp = $this->_date->timestamp(time());
        $currentDate = date('Y-m-d', $currentTimestamp);

        $date = \DateTime::createFromFormat('Y-m-d', $currentDate);
        $day = $date->format('l'); 

        $currentTimestamp = $this->_date->timestamp(time());

        $currentTime = date('H:i A', $currentTimestamp);
               
        $sqlparent = "Select slot FROM picker_slots WHERE picker_id=$picker_id and day = '".$day."'" ;
        $resultparent = $connection->fetchAll($sqlparent);
        $minslot='';
        if($resultparent){

            foreach ($resultparent as $slot){   
                print_R($slot);            
                $this->upcomingSiftTime[$picker_id] = trim(explode("-",$slot['slot'])[0]);
                $curr_slot=strtotime(trim(explode("-",$slot['slot'])[0]));
                $minslot = $curr_slot;
            }
        }
        die;
        return $minslot;
    }
    public function getFirstSlotOfPickers(){

        $objectManager = \Magento\Framework\App\ObjectManager::getInstance();
        $resource = $objectManager->get('Magento\Framework\App\ResourceConnection');
        $connection = $resource->getConnection();       

        $query = "Select id,name,email,role,type,status,active,created_at,updated_at FROM user_logins WHERE status='1' AND deleted_at IS NULL  AND  role='picker'";
        $pickers = $connection->fetchAll($query);
        $picker_slot=[];
        if($pickers){
            foreach ($pickers as $res){
                //print_r($res);
                $sql="select * from user_logins where active=0 and id=".$res['id'];
                $isActive = $connection->fetchAll($sql);
               
                if(isset($isActive[0]) && !$this->checkUserCheckin($res['id'])){                
                    $slotmin=$this->getFirstSlotOfPicker($res['id']);
                                       
                    if(!empty($slotmin)){
                        $notifybeforetime=$slotmin-(($this->checkin_notify_before_hours)*60*60);
                        
                        if($notifybeforetime>=$this->dateZone(date('H:i:s A'))){                       
                            $picker_slot[]=$res['id'];
                        }
                    }
                }
            }
            die;
        }
        if(isset($picker_slot) && !empty($picker_slot)){
            foreach($picker_slot as $key =>$val){
                $notification_params=[
                    'order_id'=>0,
                    'user_id'=>$val,
                    'user_type'=>'picker',
                    'notification_type'=>'normal',
                    'channel'=>$this->PICKER_CHANNEL.$val,
                    'type'=>"checkin-before-notify",
                    'subject'=>"Checkin Notification.",
                    'message'=>"Schedule Alert \n\nYou have scheduled yourself to be available to shop from ".$this->upcomingSiftTime[$val]." Check in now.",
                    'platform'=>'ios',
                    'created_at'=>date('Y-m-d h:i:s')
                ];

                $resp= $this->pushNotification($notification_params);
            }
        }
       
        return true;
    }

