    public function getEarnings($picker_id,$type='pending'){
        $status = [];
        if($type=='pending'){
            $status = ['placed','progress','invoiced','checkout'];
        }else{
            $status = ['placed','progress','invoiced','checkout','assigned_to_driver','canceled','complete','delivered','packed','pickedup'];
        }

        $orders = $this->_orderData->getCollection()->addFieldToSelect('*')
                  ->addFieldToFilter('main_table.status', array('in' => $status))                    
                  ->join(array('picker_order'), 'main_table.entity_id= picker_order.order_id AND picker_order.picker_id='.$picker_id,[
                      'total_earnings',
                      'tip_amount',                       
                    ]);
        $totalEarning = 0;
        $tipAmount = 0; 
        if($orders){  
            $totalEarning = 0;
            $tipAmount = 0;          
            foreach ($orders as $order) {                        
                $totalEarning = $totalEarning + $order->getData('total_earnings');
                $tipAmount = $tipAmount+$order->getData('tip_amount');
            }
            return array('total_earnings'=>(string)$totalEarning,'tip_amount'=>(string)$tipAmount);
        }else{
            return array('total_earnings'=>(string)$totalEarning,'tip_amount'=>(string)$tipAmount);
        }
    } 


    public function getTaskDetailOfPicker($picker_id,$taskfilter,$filterval){
       
	    if($taskfilter=='days'){
            return $this->getTaskByDay($picker_id,$filterval,$taskfilter);
        }
        else if($taskfilter=='weeks'){
            $filt=explode(',',$filterval);
            $datefrom=$filt[0];
            $dateto=$filt[1];

            return $this->common_helper->getTaskByWeek($picker_id,$datefrom,$dateto);
        }
        else if($taskfilter=='months'){
            $filt=explode('-',$filterval);
            $month=$filt[1];
            $year=$filt[0];
            return $this->common_helper->getTaskByMonth($picker_id,$month,$year);

        }
    }
